(function () {

    if (document.querySelector(".inner")) {
        let left = 0
        let cont = 1
        const imagesToShow = parseInt(document.querySelectorAll(".inner *").length)

        setInterval(() => {

            if (cont++ <= imagesToShow) {
                document.querySelector(".inner").style.left = `${left}vw`
                left -= 100
            } else {
                cont = 1
                left = 0
            }

        }, 2000)
    }

})()

